
const fastify = require('fastify')()
fastify.use((req, res, next) => {
  console.log('request coming')
  console.log(req.headers)
  next()
})

fastify.route({
  method: 'POST',
  url: '/',
  handler: async (req, res) => {
    console.log('pinged!!')
    res.send({ success: true, message: 'Hello World' })
  }
})

fastify.listen(8080)
